function [] = analizar_audio(nombre_archivo)  ##---------------------------------------------------------------------------##  LECTURA DE LA SEÑAL DE VOZ##---------------------------------------------------------------------------  % y: es la señal recibida, fm: es la frecuencia de muestreo  % [y, fm] = audioread(nombre_archivo);  [y, fm] = audioread("dataset-ajustes/atras_1.wav");  yo = y; % y original para graficar si nos sirve  fm    % Si la señal recibida se encuentra como vector columna, se tranforma a vector fila  if(length(y(1,:)) < length(y(:,1)))    y = y';     endif    sound(y)  % para escuchar el audio  ##---------------------------------------------------------------------------##  FILTRADO##---------------------------------------------------------------------------##---------------------------------------------------------------------------##  EXTRACCION DE CARACTERISTICAS (Coeficientes cepstrales en escala de Mel)##---------------------------------------------------------------------------    ##---------------------------------------------------------------------------##  CORRELACION CRUZADA##---------------------------------------------------------------------------##---------------------------------------------------------------------------##  IDENTIFICACION DE COMANDO##---------------------------------------------------------------------------##---------------------------------------------------------------------------##  RESULTADO##---------------------------------------------------------------------------endfunction  